package Operaters;

public class _1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		operaters(23, 15);

	}
	static void operaters(int a,int b) {
		System.out.println("The + operater values is : "+(a+b));
		System.out.println("The - operater values is : "+(a-b));
		System.out.println("The * operater values is : "+a*b);
		System.out.println("The / operater values is : "+(a/b));
		
	}

}
