package Strings;
//Extract a string using Substring

public class _4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="good dog always takes care";
		int firstindex=4;
		int lastindex=8;
		System.out.println("sub string staring from 4 index : "+s.substring(firstindex));
		System.out.println("sub string staring from 1 index and ending with 8 index : "+s.substring(1,lastindex));

	}

}
